def create_sector_df(original_df, sectors, countries):
  sector_df = original_df.loc[tuple(sectors)]
  for country in countries:
    sector_df[f"Var. {country}"] = ((sector_df['Price'] - sector_df[f"{country}"])/sector_df['Price']) * 100
  return sector_df

